import React from 'react';

class Status extends React.Component {
    constructor(props) {
        super (props);
        this.state= {
            id: this.props.id, 
            status: 'Open',
        }
        this.statusHandleChange = this.statusHandleChange.bind(this);
        this.statusHandleSubmit = this.statusHandleSubmit.bind(this);
    }

    statusHandleChange(event, key) {
        this.setState({[key]: event.target.value});
    };

    statusHandleSubmit(event) {
        event.preventDefault();
        console.log('user has changed the status');
        fetch('/status', {
        method: 'POST', 
        headers: {
            "Content-Type": 'application/json'
        },
        body: JSON.stringify({
            id: this.state.id,
            status: this.state.status,
        })
     })
     .then(function(response) {
        return 

    }).then(function(body) {

        console.log(body);
    })
    };

    render () {
    return(
        <div className="status">
       <form >
           <label><select name="Status" onChange={this.statusHandleSubmit}>
                    <option selected="selected">Open</option>
                    <option>Closed</option>
                    <option>Not Applicable</option>
                    </select>
                    <noscript><input type="submit" value="Submit"/>
                    </noscript></label></form>
        </div>
        )}
    };

    export default Status;
