import React from 'react';
import Login from './login.jsx';
import Notes from './Notes.jsx';
import Status from './status.jsx';
import Delete from './delete.jsx';
import withFirebaseAuth from 'react-with-firebase-auth';
import * as firebase from 'firebase/app';
import 'firebase/auth';
import Config from '../firebaseConfig';


const firebaseAppAuth = Config.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = 
        {
            prospects: [],
        }
    }

 componentDidMount() {
     fetch('/getProspectData')
     .then(response => response.json())
     .then(prospects => this.setState({ prospects })); 
     };

renderData() {
    return this.state.prospects.map((prospect, index) => {
        var { id, name, email, phone, caseType, comments, createdAt, notes, status } = prospect
        return (
            <tr key={id}>
                <td>{id}</td>
                <td>{name}</td>
                <td>{email}</td>
                <td>{phone}</td>
                <td>{caseType}</td>
                <td>{comments}</td>
                <td>
                {Intl.DateTimeFormat('en-US', { 
                    year: 'numeric', 
                    month: 'long', 
                    day: '2-digit' 
                    }).format({createdAt})}
                </td>
                <td>
                    <Notes id={id} notes={notes}/></td> 
                <td>
                    <Status id={id} status={status}/>
                </td>
                <td>
                    <Delete id={id}/>
                </td>
            </tr>
           )
        })
     }

render() { 

    return (
        // <p> {JSON.stringify(clients)}</p>
     <div>
        <div className="Logins">
            <Login 
            user={this.props.user}
            signInWithGoogle={this.props.signInWithGoogle}
            signOut={this.props.signOut}/>
        </div>
        <div>
            <h1 id='title'>Prospects:</h1>
            <table id='prospects'>
            <thead>
            <tr>
                <th>Client ID</th>
                <th>Name</th>
                <th>Email Address</th>
                <th>Phone Number</th>
                <th>Case Type</th>
                <th>Comments</th>
                <th>Entry Date</th>
                <th>Notes </th>
                <th>Status </th>
            </tr> 
            </thead>
            <tbody>
              {this.renderData()}
            </tbody>
            </table>
         </div>
        </div>
    )
}
};

export default withFirebaseAuth({
    providers,
    firebaseAppAuth,
})(Table);
