import React from 'react';

class Table extends React.Component {
    constructor() {
        super();
        this.state = 
        {
            prospects: [],
        }
    }

 componentDidMount() {
     fetch('/getProspectData')
     .then(response => response.json())
     .then(prospects => this.setState({ prospects })); 
     };

renderData() {
    return this.state.prospects.map((prospect, index) => {
        const { id, name, email, phone, caseType, comments, notes, status, createdAt } = prospect//destructuring
        return (
            <tr key={id}>
                <td>{id}</td>
                <td>{name}</td>
                <td>{email}</td>
                <td>{phone}</td>
                <td>{caseType}</td>
                <td>{comments}</td>
                <td>{notes}</td>
                <td>{status}</td>
                <td>{createdAt}</td>
            </tr>
           )
        })
     }

renderHeader() {
    let header = Object.keys(this.state.prospects[0])
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
        })
     }


render() { 
    // const clients = (this.state.data)
    return (
        // <p> {JSON.stringify(clients)}</p>
        <div>
            <h1 id='title'>Prospects</h1>
            <table id='prospects'>
               <tbody>
                  {this.renderData()}
               </tbody>
            </table>
         </div>
    )
}
};

export default Table;
